<?php $jsonData = file_get_contents('../../../datasources/system.json');$data = json_decode($jsonData, true);$jsonData2 = file_get_contents('../../../datasources/clients.json');$data2 = json_decode($jsonData2, true);$conn = pg_connect("host='" . $data['host'] . "' port='" . $data['port'] . "' dbname='" . $data['database'] . "' user='" . $data['username'] . "' password='" . $data['password'] . "'");if (!$conn) exit;echo "[{\"login_system\":" . json_encode($data) . "}";echo ", {\"login_clients\":" . json_encode($data2) . "}";$management_servers = pg_fetch_all(pg_query($conn, "SELECT * FROM admin.management_servers;"));echo ", {\"management_servers\":" . json_encode($management_servers) . "}";$mta_servers = pg_fetch_all(pg_query($conn, "SELECT * FROM admin.mta_servers;"));echo ", {\"mta_servers\":" . json_encode($mta_servers) . "}";$smtp_users = pg_fetch_all(pg_query($conn, "SELECT * FROM admin.smtp_users;"));echo ", {\"smtp_users\":" . json_encode($smtp_users) . "}";$data_lists = pg_fetch_all(pg_query($conn, "SELECT * FROM lists.data_lists;"));echo ", {\"data_lists\":" . json_encode($data_lists) . "}";$affiliate_networks = pg_fetch_all(pg_query($conn, "SELECT * FROM affiliate.affiliate_networks;"));echo ", {\"affiliate_networks\":" . json_encode($affiliate_networks) . "}]";pg_close($conn); ?>
